/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useState } from "react";
import axios from "axios";
import Blogs from "./Blogs";
import { makeStyles } from "@mui/styles";
import config from "../config";

const useStyles = makeStyles((theme) => ({
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    margin: "20px auto",
    width: "80%",
  },
  blogContainer: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "10px",
    marginBottom: "20px",
    boxShadow: "0px 2px 4px rgba(0, 0, 0, 0.1)",
    position: "relative", // Needed for positioning the delete button
  },
  blogImage: {
    width: "100%",
    height: "auto",
    borderRadius: "10px",
    marginBottom: "10px",
  },
  deleteButton: {
    position: "absolute",
    right: 10,
    top: 10,
    color: "red",
    cursor: "pointer",
  },
}));

const UserBlogs = ({ userid }) => {
  const classes = useStyles();
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true); // Loading state
  const [error, setError] = useState(null); // Error state

  const fetchBlogs = async () => {
    try {
      const res = await axios.get(`http://localhost:5001/api/blogs/user/${userid}`);
      setBlogs(res.data.user.blogs); // Assuming response structure includes `user` and `blogs`
    } catch (err) {
      console.error("Error fetching blogs:", err);
      setError("Failed to fetch blogs.");
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBlogs();
  }, [userid]); // Dependency on userid to refetch if it changes

  const handleDelete = async (blogId) => {
    try {
      await axios.delete(`${config.BASE_URL}/api/blogs/${blogId}`);
      // Update the state directly to remove the deleted blog
      setBlogs((prevBlogs) => prevBlogs.filter(blog => blog._id !== blogId));
    } catch (err) {
      console.error("Error deleting blog:", err);
      setError("Failed to delete blog.");
    }
  };

  return (
    <div className={classes.container}>
      {loading && <p>Loading...</p>}
      {error && <p>{error}</p>}
      {blogs.map((blog) => (
        <div key={blog._id} className={classes.blogContainer}>
          <Blogs
            id={blog._id}
            isUser={true}
            title={blog.title}
            description={blog.description}
            imageURL={blog.image}
            userName={blog.userName} // Adjust if necessary
          />
          <img
            className={classes.blogImage}
            src={blog.image}
            alt={blog.title}
          />
          <span
            className={classes.deleteButton}
            onClick={() => handleDelete(blog._id)}
          >
            Delete
          </span>
        </div>
      ))}
    </div>
  );
};

export default UserBlogs;
