/* eslint-disable react-hooks/exhaustive-deps */
import React, { useEffect, useState } from "react";
import axios from "axios";
import Blogs from "./Blogs";
import { makeStyles } from "@mui/styles";
import config from "../config";

const useStyles = makeStyles((theme) => ({
  container: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    margin: "20px auto",
    width: "80%",
  },
  blogContainer: {
    display: "flex",
    flexDirection: "column",
    alignItems: "center",
    padding: "20px",
    border: "1px solid #ccc",
    borderRadius: "10px",
    marginBottom: "20px",
    boxShadow: "0px 2px 4px rgba(0, 0, 0, 0.1)",
    position: "relative", // Needed for absolutely positioned elements
  },
  blogImage: {
    width: "100%",
    height: "auto",
    borderRadius: "10px",
    marginBottom: "10px",
  },
  deleteButton: {
    position: "absolute",
    right: 10,
    top: 10,
    color: "red",
    cursor: "pointer",
  },
}));

const UserBlogs = ({ blogid }) => {
  const classes = useStyles();
  const [blogs, setBlogs] = useState([]);
  const [loading, setLoading] = useState(true); // Loading state for better UX

  const fetchBlogs = async () => {
    try {
      const res = await axios.get(`http://localhost:5000/api/blogs/user/${blogid}`);
      setBlogs(res.data.user.blogs); // Assuming the response includes `user` and `blogs`
    } catch (err) {
      console.error("Error fetching blogs:", err);
    } finally {
      setLoading(false);
    }
  };

  useEffect(() => {
    fetchBlogs();
  }, [blogid]); // Dependency on blogid to refetch if it changes

  const handleDelete = async (blogId) => {
    try {
      await axios.delete(`${config.BASE_URL}/api/blogs/${blogId}`);
      // Update the state to reflect the deletion
      setBlogs((prevBlogs) => prevBlogs.filter(blog => blog._id !== blogId));
    } catch (err) {
      console.error("Error deleting blog:", err);
    }
  };

  return (
    <div className={classes.container}>
      {loading ? (
        <p>Loading...</p> // Show a loading message or spinner
      ) : (
        blogs.map((blog) => (
          <div key={blog._id} className={classes.blogContainer}>
            <Blogs
              id={blog._id}
              isUser={true}
              title={blog.title}
              description={blog.description}
              imageURL={blog.image}
              userName={blog.userName} // Adjust based on your actual data
            />
            <img
              className={classes.blogImage}
              src={blog.image}
              alt={blog.title}
            />
            <span
              className={classes.deleteButton}
              onClick={() => handleDelete(blog._id)}
            >
              Delete
            </span>
          </div>
        ))
      )}
    </div>
  );
};

export default UserBlogs;
